mapper.select:
SELECT (SELECT COURSETITLE FROM LMS_ED_COURSE WHERE COURSENO = A.COURSENO ) AS COURSETITLE ,(SELECT MBERNM FROM LMS_CT_UNITY_MBER WHERE UNITY_MBERNO = A.USERNO ) AS WRITER , A.CONTENT , A.COURSENO , TO_CHAR(A.WDATE, 'YYYY-MM-DD') AS WDATE , A.SCORE FROM LMS_ED_REVIEW A , LMS_ED_COURSE_SEQ B WHERE A.USEYN = 'Y' AND A.CSEQNO = B.CSEQNO AND B.USEYN = 'Y'
AND A.COURSENO = #{courseno}
ORDER BY A.REVIEWNO DESC

SELECT
    CONTENT
    , TO_CHAR(WDATE, 'YYYY-MM-DD') AS WDATE
    , SCORE
FROM
    LMS_ED_REVIEW
WHERE
    CSEQNO = #{searchCseqno}
    AND COURSENO = #{courseno}
    AND USERNO = #{SES_USERNO}

mapper.insert:
    	INSERT INTO
		    LMS_ED_REVIEW
		    (
		        REVIEWNO
		        , COURSENO
		        , CSEQNO
		        , USERNO
		        , CONTENT
		        , WDATE
		        , USEYN
		        , SCORE
		    )
		VALUES
		    (
		        (SELECT NVL(MAX(REVIEWNO),0)+1 FROM LMS_ED_REVIEW)
		        , #{courseno}
		        , #{searchCseqno}
		        , #{SES_USERNO}
		        , #{rvContent}
		        , SYSDATE
		        , 'Y'
		        , #{rvScore}
		    )

mapper.update:
    UPDATE
	    LMS_ED_REVIEW
	SET
	    CONTENT = #{rvContent}
	    , SCORE = #{rvScore}
	    , WDATE = SYSDATE
	WHERE
	    CSEQNO = #{searchCseqno}
	    AND COURSENO = #{courseno}
    	AND USERNO = #{SES_USERNO}

mapper.delete:
    DELETE FROM
	    LMS_ED_REVIEW
	WHERE
	    CSEQNO = #{searchCseqno}
	    AND COURSENO = #{courseno}
    	AND USERNO = #{SES_USERNO}
